import com.aventstack.extentreports.AnalysisStrategy;
import com.aventstack.extentreports.ExtentReports;
import com.aventstack.extentreports.ExtentTest;
import com.aventstack.extentreports.Status;
import com.aventstack.extentreports.reporter.ExtentSparkReporter;
import com.aventstack.extentreports.reporter.configuration.Theme;
import com.aventstack.extentreports.markuputils.*;


// Create GroovyUtils
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
// Get the project path
def projectPath = groovyUtils.projectPath.toString()
// Get project name
def projectName = runner.getProject().getName()
log.info projectPath
log.info projectName
log.info runner.getProject().getPath()

def sparkReporter = new ExtentSparkReporter(System.getProperty('user.dir') + "\\target/surefire-reports/extent.html");
def extent = new ExtentReports();


sparkReporter.config().setEncoding("utf-8")
sparkReporter.config().setDocumentTitle(projectName + " report")
sparkReporter.config().setReportName(projectName + " results")
sparkReporter.config().setTheme(Theme.DARK)

//extent.setSystemInfo
extent.attachReporter(sparkReporter)

def testSuite = runner.getProject().getTestSuiteByName("CalculatorSoapTestSuite")
def testSuiteName = testSuite.name
log.info testSuiteName
//Test suite left panel display
def extentTest = extent.createTest(testSuiteName)
//---------------------------------------------------------------------------
def i = 0;
for(testCase in testSuite.getTestCaseList()) {
	log.info i;
	i++

	def testCaseName = testCase.name
	log.info testCaseName

	def testCaseNode = extentTest.createNode(testCaseName)

	for(testStep in testCase.getTestStepList()) {
		def testStepName = testStep.name
		log.info testStepName

		log.info testStep.getProperty("RawRequest").getValue()
		log.info testStep.testRequest.getResponse().getRequest().getRequestContent()
		log.info testStep.testRequest.getResponse().getResponseHeaders()
		log.info testStep.testRequest.getResponse().getContentAsString()

		def finalTestStepStatus
		def testStepStatusList = new ArrayList<>()
		def testAssertionsList = new ArrayList<>()
		if (!testStep.getAssertionList().isEmpty()) {
			for (testAssertion in testStep.getAssertionList()) {

				log.info testAssertion.getStatus()
				log.info testAssertion.getLabel()
				log.info testAssertion.getAssertable().getAssertableContentAsXml()
				//if (testAssertion.getStatus().toString().equalsIgnoreCase('FAILED'))
					//log.info "Expected: " + testAssertion.ID + " " + testAssertion.getLabel() + " "  + testAssertion.getToken()
				//else {
					//log.info "Expected: " + testAssertion.ID + " " + testAssertion.getLabel()
				//}

				testStepStatus = testAssertion.getStatus().toString()

				testStepStatusList.add(testStepStatus)

				testStepLabel = testAssertion.getLabel()

				if (testStepStatus.equalsIgnoreCase('FAILED')) {
					log.info testAssertion.getErrors()[0].getMessage()
					assertionError = testAssertion.getErrors()[0].getMessage()
				} else {
					assertionError = "Assertion Passed"
				}

				testAssertionsList.add("AssertionType: " + testStepLabel + "\n" + "AssertionStatus: " + testStepStatus + "\n" + "AssertionMessage: " + assertionError)

			}

			def assertionCode = '';
			for (s in testAssertionsList) {
				assertionCode = assertionCode + "\n" + s
			}

			def markup = MarkupHelper.createCodeBlock(testStep.getProperty("RawRequest").getValue(), testStep.testRequest.getResponse().getResponseHeaders().toString() + "\n" + testStep.getProperty("Response").getValue(), assertionCode)

			if (testStepStatusList.contains('FAILED')) {
					testCaseNode.createNode(testStepName).fail(markup)
			} else {
				testCaseNode.createNode(testStepName).pass(markup)
			}

			//testStepLabel = testStep.getAssertionList().get(0).getLabel()
			//if (testStepStatus.equalsIgnoreCase('FAILED')) {
			//	log.info testStep.getAssertionList().get(0).getErrors()[0].getMessage()
			//	testStepError = testStep.getAssertionList().get(0).getErrors()[0].getMessage()
			//} else {
			//	testStepError = "Test Passed"
			//}
			//def assertionCode = "AssertionType: " + testStepLabel + "\n" + "AssertionStatus: " + testStepStatus + "\n" + "AssertionMessage: " + testStepError

			//def markup = MarkupHelper.createCodeBlock(testStep.getProperty("RawRequest").getValue(), testStep.testRequest.getResponse().getResponseHeaders().toString() + "\n" + testStep.getProperty("Response").getValue(), assertionCode)

			//if (testStepStatus.equalsIgnoreCase('FAILED')) {
			//	testCaseNode.createNode(testStepName).fail(markup)
			//} else {
			//	testCaseNode.createNode(testStepName).pass(markup)
			//}
		}


	}
}

